import React, { Component } from "react";
import { Table } from "reactstrap";
import NewFoodModal from "./NewFoodModal";

import ConfirmRemovalModal from "./ConfirmRemovalModal";

class FoodList extends Component {
  render() {
    const food = this.props.food;
    let total = 0;
      var month = new Date().getMonth() + 1;
      var day = new Date().getDate();
      var year = new Date().getFullYear();
      var date = new Date();
    return (
      <table class="table table-hover">
        <thead class="thead-light">
          <tr>
            <th>Food</th>
            <th>Calories</th>
            <th>Date Entered</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {!food || food.length <= 0 ? (
             <tr>
              <td colSpan="6" align="center">
                <b>No entries for today</b>
              </td>
            </tr>
          ) : (
            food.map(food => (
                food.registrationDate == year-month-day ? (
                    total = total + food.calories,
              <tr key={food.pk}>
                <td>{food.name}</td>
                <td>{food.calories}</td>
                <td>{food.registrationDate}</td>
                <td align="center">
                  <NewFoodModal
                    create={false}
                    food={food}
                    resetState={this.props.resetState}
                  />
                  &nbsp;&nbsp;
                  <ConfirmRemovalModal
                    pk={food.pk}
                    resetState={this.props.resetState}
                  />
                </td>
              </tr>
                ) : (
                <table><tr><td>{food.registrationDate} {year}-{month}-{day}</td></tr></table>
                )

            ))
          )} </tbody>
          <tr>
              <th>Total Calories</th>
              <th>{total}</th>
              <th></th>
              <th></th>
          </tr>
      </table>
        //try splitting above table into individual modules
        //add another table with recently inputted food
    );
  }
}

export default FoodList;
